mapper.select:
select q_value
from QnA_typelist
where q_type = #{qtype}

SELECT *
FROM (
         SELECT c_no, member_id, content, c_response_at, q_no
         FROM comments
         WHERE q_no = #{qno}
         ORDER BY c_response_at DESC
         LIMIT #{start}, #{end}
     ) AS result

mapper.update:
update comments
set content=#{content}
where c_no = #{cno}
