mapper.select:
select seller_no
from seller
where company_name=#{sellerName}

SELECT ROW_NUMBER() OVER (ORDER BY p.payment_date DESC) as no, d.deli_no AS deliNo, p.payment_date AS orderDate, d.deli_status AS deliStatus, g.GOODS_NAME AS goodsName, d.deli_quan AS quantity, m.member_id AS customerName, d.deli_date AS deliveryDate from (select * FROM payments p where p.seller_id = #{sellerNo}) p JOIN delivery d ON p.payments_no = d.payments_no JOIN goods g ON d.stock_no = g.goods_no JOIN member m ON p.user_id = m.member_id
d.deli_no g.goods_name m.member_id
like #{searchWord}
ORDER BY p.payment_date DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) from (select * FROM payments p where p.seller_id = #{sellerNo}) p JOIN delivery d ON p.payments_no = d.payments_no JOIN goods g ON d.stock_no = g.goods_no JOIN member m ON p.user_id = m.member_id
d.deli_no g.goods_name m.member_id
like #{searchWord}
