mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY p.payment_date DESC) as no, p.payments_no AS deliNo, p.payment_date AS orderDate, d.deli_status AS deliStatus, g.GOODS_NAME AS goodsName, d.deli_quan AS quantity, m.member_id AS customerName, d.deli_date AS deliveryDate from (select * FROM payments p where p.seller_id = #{sellerNo}) p JOIN delivery d ON p.payments_no = d.payments_no JOIN goods g ON d.inven_goods_no = g.GOODS_NO JOIN member m ON p.user_id = m.member_id
AND (g.GOODS_NAME LIKE CONCAT('%', #{searchWord}, '%') OR m.member_id LIKE CONCAT('%', #{searchWord}, '%'))
ORDER BY p.payment_date DESC LIMIT #{offset}, #{pageSize}
